def hammingDistance(s,t):
    '''for each tuple (assigned to nt1 and nt2 variables) created by the zip built in function representing the base pairs of the 
       two dna strings the built in sum function will add 1 if nt1 and nt2 are different and 0 otherwise
    '''
    return sum(1 if nt1 != nt2 else 0 for nt1, nt2 in zip(s, t))

if __name__ == '__main__':
    s=""
    t=""
    print(hammingDistance(s, t))